var mapLink=document.querySelector(".main-contacts-modal");var mapPopup=document.querySelector(".modal-map");var mapClose=mapPopup.querySelector(".modal-close");mapLink.addEventListener("click",function(a){a.preventDefault();mapPopup.classList.add("modal-map-show")});mapClose.addEventListener("click",function(a){a.preventDefault();mapPopup.classList.remove("modal-map-show")});window.addEventListener("keydown",function(a){if(a.keyCode===27){a.preventDefault();if(mapPopup.classList.contains("modal-map-show")){mapPopup.classList.remove("modal-map-show")}}});
